import{_ as i,c as o,o as r,b1 as e}from"./chunks/framework.BSsnj4C9.js";const d=JSON.parse('{"title":"Rogue Killer 恶意软件下载网站检测与拦截系统","description":"","frontmatter":{},"headers":[],"relativePath":"projects/RougeKiller.md","filePath":"projects/RougeKiller.md"}'),t={name:"projects/RougeKiller.md"};function a(n,l,s,u,g,h){return r(),o("div",null,l[0]||(l[0]=[e('<h1 id="rogue-killer-恶意软件下载网站检测与拦截系统" tabindex="-1">Rogue Killer 恶意软件下载网站检测与拦截系统 <a class="header-anchor" href="#rogue-killer-恶意软件下载网站检测与拦截系统" aria-label="Permalink to &quot;Rogue Killer 恶意软件下载网站检测与拦截系统&quot;">​</a></h1><h2 id="项目概述" tabindex="-1">项目概述 <a class="header-anchor" href="#项目概述" aria-label="Permalink to &quot;项目概述&quot;">​</a></h2><p>Rogue Killer 是一个综合性的恶意软件下载网站检测与拦截系统，旨在保护用户免受恶意网站的侵害。系统通过浏览器插件实时拦截恶意网站，结合后端AI分析和前端可视化展示，为用户提供全方位的网络安全保护。</p><h2 id="系统架构" tabindex="-1">系统架构 <a class="header-anchor" href="#系统架构" aria-label="Permalink to &quot;系统架构&quot;">​</a></h2><p>系统采用微服务架构，包含四个核心模块：</p><ul><li><strong>浏览器插件</strong>：实时拦截恶意网站访问</li><li><strong>前端界面</strong>：提供用户交互和数据可视化</li><li><strong>后端服务</strong>：处理业务逻辑和AI分析</li><li><strong>爬虫模块</strong>：收集和分析网站数据</li></ul><h2 id="技术栈详情" tabindex="-1">技术栈详情 <a class="header-anchor" href="#技术栈详情" aria-label="Permalink to &quot;技术栈详情&quot;">​</a></h2><h3 id="_1-浏览器插件-chrome-extension" tabindex="-1">1. 浏览器插件 (Chrome Extension) <a class="header-anchor" href="#_1-浏览器插件-chrome-extension" aria-label="Permalink to &quot;1. 浏览器插件 (Chrome Extension)&quot;">​</a></h3><p><strong>技术栈：</strong></p><ul><li>Manifest V3 (最新Chrome扩展标准)</li><li>JavaScript ES6+</li><li>jQuery (DOM操作)</li><li>declarativeNetRequest API (网络请求拦截)</li><li>Chrome Storage API (数据存储)</li><li>多语言国际化支持 (i18n)</li></ul><p><strong>主要功能：</strong></p><ul><li>实时拦截Google、百度、Bing、搜狗搜索结果中的恶意网站</li><li>支持用户手动添加/删除黑名单</li><li>提供黑名单导入/导出功能</li><li>支持一键同步黑名单数据</li><li>智能识别搜索结果中的恶意链接并隐藏</li><li>提供友好的用户界面进行黑名单管理</li></ul><p><strong>核心特性：</strong></p><ul><li>支持主流搜索引擎的搜索结果过滤</li><li>使用MutationObserver监听DOM变化，动态更新搜索结果</li><li>提供上下文菜单快速添加黑名单</li><li>支持PWS (Personal Web Search) 功能配置</li></ul><h3 id="_2-前端界面-vue-3" tabindex="-1">2. 前端界面 (Vue 3) <a class="header-anchor" href="#_2-前端界面-vue-3" aria-label="Permalink to &quot;2. 前端界面 (Vue 3)&quot;">​</a></h3><p><strong>技术栈：</strong></p><ul><li>Vue 3.2.13 (组合式API)</li><li>Element Plus 2.9.7 (UI组件库)</li><li>Vue Router 4.5.0 (路由管理)</li><li>Pinia 3.0.1 (状态管理)</li><li>Pinia Plugin Persistedstate 4.2.0 (状态持久化)</li><li>Axios 1.8.4 (HTTP客户端)</li><li>ECharts 5.6.0 (数据可视化)</li><li>Markdown-it 14.1.0 (Markdown渲染)</li></ul><p><strong>主要功能：</strong></p><ul><li>恶意网站数据可视化展示</li><li>网站详情查看和审核功能</li><li>用户认证和权限管理</li><li>数据统计和分析报表</li><li>响应式设计，支持多端访问</li></ul><p><strong>页面模块：</strong></p><ul><li>网站列表和搜索</li><li>网站详情查看</li><li>数据审核管理</li><li>系统设置</li><li>用户认证</li><li>数据追踪</li></ul><h3 id="_3-后端服务-spring-boot" tabindex="-1">3. 后端服务 (Spring Boot) <a class="header-anchor" href="#_3-后端服务-spring-boot" aria-label="Permalink to &quot;3. 后端服务 (Spring Boot)&quot;">​</a></h3><p><strong>技术栈：</strong></p><ul><li>Spring Boot 3.4.4</li><li>Java 17</li><li>Spring Web MVC (RESTful API)</li><li>Spring AOP (面向切面编程)</li><li>MyBatis Plus 3.5.9 (ORM框架)</li><li>MySQL 8.0 (数据库)</li><li>Druid 1.1.16 (数据库连接池)</li><li>JJWT 0.12.6 (JWT认证)</li><li>Spring WebSocket (实时通信)</li><li>阿里云DashScope SDK 2.18.3 (AI能力集成)</li></ul><p><strong>主要功能：</strong></p><ul><li>用户认证和权限管理</li><li>恶意网站数据存储和管理</li><li>AI模型集成和分析</li><li>实时数据推送</li><li>批量数据处理</li><li>系统监控和日志记录</li></ul><p><strong>核心特性：</strong></p><ul><li>JWT无状态认证</li><li>AOP统一日志和异常处理</li><li>数据库读写分离配置</li><li>分页查询优化</li><li>WebSocket实时通知</li><li>集成通义千问AI能力</li></ul><h3 id="_4-爬虫模块-python" tabindex="-1">4. 爬虫模块 (Python) <a class="header-anchor" href="#_4-爬虫模块-python" aria-label="Permalink to &quot;4. 爬虫模块 (Python)&quot;">​</a></h3><p><strong>技术栈：</strong></p><ul><li>Python 3.11</li><li>Crawl4AI 0.6.3 (异步爬虫框架)</li><li>Playwright (浏览器自动化)</li><li>MySQL Connector (数据库操作)</li><li>asyncio (异步编程)</li></ul><p><strong>当前实现功能：</strong></p><ul><li>网站内容爬取和解析</li><li>域名和子域名提取</li><li>IP地址解析</li><li>网页标题和内容提取</li><li>数据格式化处理</li><li>MySQL数据库存储</li></ul><p><strong>数据处理流程：</strong></p><ol><li>输入URL列表</li><li>异步爬取网页内容</li><li>提取关键信息： <ul><li>域名和子域名</li><li>服务器IP地址</li><li>网页标题和内容</li><li>端口信息</li></ul></li><li>数据清洗和格式化</li><li>存储到MySQL数据库</li></ol><h2 id="系统特色" tabindex="-1">系统特色 <a class="header-anchor" href="#系统特色" aria-label="Permalink to &quot;系统特色&quot;">​</a></h2><ol><li><strong>实时防护</strong>：浏览器插件实时拦截恶意网站访问</li><li><strong>智能分析</strong>：集成AI能力进行网站威胁分析</li><li><strong>多引擎支持</strong>：支持主流搜索引擎的恶意链接过滤</li><li><strong>数据可视化</strong>：提供直观的数据展示和分析报表</li><li><strong>可扩展架构</strong>：模块化设计，便于功能扩展</li></ol><h2 id="应用场景" tabindex="-1">应用场景 <a class="header-anchor" href="#应用场景" aria-label="Permalink to &quot;应用场景&quot;">​</a></h2><ul><li>个人用户网络安全防护</li><li>企业网络安全监控</li><li>教育机构网络安全教育</li><li>网络安全研究数据分析</li></ul><h2 id="技术亮点" tabindex="-1">技术亮点 <a class="header-anchor" href="#技术亮点" aria-label="Permalink to &quot;技术亮点&quot;">​</a></h2><ol><li><strong>先进的浏览器扩展技术</strong>：使用Manifest V3和declarativeNetRequest API</li><li><strong>现代化的前端架构</strong>：Vue 3 + Element Plus + Pinia组合</li><li><strong>高性能后端服务</strong>：Spring Boot + MyBatis Plus + MySQL</li><li><strong>智能爬虫系统</strong>：基于Crawl4AI的异步爬虫框架</li><li><strong>AI集成能力</strong>：通义千问AI模型集成</li></ol>',41)]))}const c=i(t,[["render",a]]);export{d as __pageData,c as default};
